package buildNodes;

public class ArrayIndex {
	int row;
	int column;
	
	public ArrayIndex(int row, int column) {
		// TODO Auto-generated constructor stub
		this.row = row;
		this.column = column;
	}

	public int getRow() {
		return row;
	}

	public void setRow(int row) {
		this.row = row;
	}

	public int getColumn() {
		return column;
	}

	public void setColumn(int column) {
		this.column = column;
	}

}
